custom_css:
/* Estilo con contraste alto y estética profesional */

body {
  scroll-behavior: smooth;
  background-color: #ffffff;
  color: #111111;
  font-family: 'Segoe UI', Roboto, sans-serif;
  line-height: 1.6;
  font-size: 16px;
}

main {
  max-width: 900px;
  margin: 0 auto;
}

h1, h2, h3, h4, h5 {
  color: #1e3a8a;
  margin-top: 2rem;
  margin-bottom: 1rem;
  font-weight: 700;
  line-height: 1.25;
}

nav ul {
  list-style: none;
  padding-left: 0;
}

nav li {
  margin: 0.6em 0;
  font-size: 0.96em;
}

nav a {
  text-decoration: none;
  padding: 0.25em 0.5em;
  border-radius: 5px;
  color: #1d4ed8;
  display: block;
  transition: all 0.2s ease-in-out;
}

nav a:hover {
  background-color: #dbeafe;
  color: #1e3a8a;
  font-weight: 600;
}

main code, pre {
  background-color: #f3f4f6;
  color: #111827;
  padding: 1rem;
  border-radius: 8px;
  font-family: 'Fira Code', monospace;
  font-size: 0.95em;
  overflow-x: auto;
  box-shadow: inset 0 0 0 1px #e5e7eb, 0 2px 6px rgba(0, 0, 0, 0.06);
  margin-bottom: 1.5rem;
  line-height: 1.5;
}

pre code {
  background: none;
  padding: 0;
  border: none;
  box-shadow: none;
}

a {
  color: #2563eb;
  text-decoration: underline;
}

a:hover {
  color: #1e40af;
  background-color: #f0f9ff;
}

section {
  padding-bottom: 5rem;
}
